import React, { useRef, useState } from 'react';
import { Link } from 'react-router-dom';

const IMG_W500 = 'https://image.tmdb.org/t/p/w500';

/* ── Single Poster Card ───────────────────────────────────────────── */
const PosterCard = ({ id, title, original_title, posterUrl, poster_path }) => {
  const [err, setErr] = useState(false);
  const name = title || original_title || '';

  const getSrc = () => {
    if (err) return null;
    if (posterUrl) {
      if (posterUrl.startsWith('http')) return posterUrl;
      if (posterUrl.startsWith('/')) return `${IMG_W500}${posterUrl}`;
    }
    if (poster_path) return `${IMG_W500}${poster_path}`;
    return null;
  };

  const src = getSrc();

  return (
    <Link to={`/movie/${id}`} className="block outline-none flex-shrink-0" style={{ width: '160px' }}>
      <div className="rounded-xl overflow-hidden cursor-pointer transition-all duration-300 hover:scale-105 hover:shadow-2xl group"
        style={{ background: '#1a1b2e', border: '1px solid rgba(255,255,255,0.06)' }}>

        {/* Poster image */}
        <div className="relative overflow-hidden" style={{ paddingBottom: '150%' }}>
          {src && !err ? (
            <img
              src={src}
              alt={name}
              onError={() => setErr(true)}
              className="absolute inset-0 w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
              loading="lazy"
            />
          ) : (
            /* Fallback — gradient with title */
            <div className="absolute inset-0 flex flex-col items-center justify-center p-3 text-center"
              style={{ background: 'linear-gradient(135deg, #1e1f35 0%, #2d1f3d 50%, #1a1b2e 100%)' }}>
              <div className="w-12 h-12 rounded-xl mb-3 flex items-center justify-center"
                style={{ background: 'rgba(229,25,55,0.15)', border: '1px solid rgba(229,25,55,0.3)' }}>
                <svg viewBox="0 0 24 24" fill="none" stroke="#e51937" strokeWidth="1.5" width="24" height="24">
                  <path d="M7 4v16M17 4v16M3 8h4m10 0h4M3 16h4m10 0h4M4 4h16a1 1 0 011 1v14a1 1 0 01-1 1H4a1 1 0 01-1-1V5a1 1 0 011-1z"/>
                </svg>
              </div>
              <p className="text-white text-xs font-bold leading-tight line-clamp-3">{name}</p>
            </div>
          )}

          {/* Hover overlay */}
          <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-end"
            style={{ background: 'linear-gradient(to top, rgba(229,25,55,0.85) 0%, transparent 55%)' }}>
            <p className="text-white text-xs font-bold p-3 w-full">Book Now →</p>
          </div>
        </div>

        {/* Title */}
        <div className="p-2.5">
          <h4 className="text-white text-xs font-semibold line-clamp-2 leading-snug">{name}</h4>
        </div>
      </div>
    </Link>
  );
};

/* ── Arrow Button ─────────────────────────────────────────────────── */
const ArrowBtn = ({ dir, onClick, disabled }) => (
  <button
    onClick={onClick}
    disabled={disabled}
    className="flex-shrink-0 w-10 h-10 rounded-full flex items-center justify-center transition-all duration-200"
    style={{
      background: disabled ? 'rgba(255,255,255,0.04)' : 'rgba(229,25,55,0.15)',
      border: `1px solid ${disabled ? 'rgba(255,255,255,0.08)' : 'rgba(229,25,55,0.4)'}`,
      cursor: disabled ? 'not-allowed' : 'pointer',
      opacity: disabled ? 0.4 : 1,
    }}
  >
    <svg viewBox="0 0 24 24" fill="none" stroke={disabled ? '#6b7280' : '#e51937'} strokeWidth="2.5"
      width="16" height="16">
      <path strokeLinecap="round" strokeLinejoin="round"
        d={dir === 'left' ? 'M15 19l-7-7 7-7' : 'M9 5l7 7-7 7'} />
    </svg>
  </button>
);

/* ── Main Slider ──────────────────────────────────────────────────── */
const PosterSlider = ({ title, subtitle, posters = [] }) => {
  const trackRef  = useRef(null);
  const [pos, setPos] = useState(0);   // how many cards we've scrolled

  if (!posters.length) return null;

  const CARD_W    = 168;   // 160px card + 8px gap
  const VISIBLE   = Math.floor((window.innerWidth * 0.85) / CARD_W) || 5;
  const maxPos    = Math.max(0, posters.length - VISIBLE);

  const slide = (dir) => {
    const next = Math.min(Math.max(pos + dir * Math.floor(VISIBLE / 2), 0), maxPos);
    setPos(next);
    if (trackRef.current) {
      trackRef.current.scrollTo({ left: next * CARD_W, behavior: 'smooth' });
    }
  };

  // Sync pos on scroll
  const onScroll = () => {
    if (trackRef.current) {
      setPos(Math.round(trackRef.current.scrollLeft / CARD_W));
    }
  };

  return (
    <div className="animate-fade-up">
      {/* Header */}
      <div className="flex items-center justify-between mb-5">
        <div>
          <h3 className="text-white text-xl font-bold">{title}</h3>
          {subtitle && <p className="text-gray-500 text-xs mt-0.5">{subtitle}</p>}
        </div>
        <div className="flex items-center gap-2">
          <ArrowBtn dir="left"  onClick={() => slide(-1)} disabled={pos === 0} />
          <ArrowBtn dir="right" onClick={() => slide(1)}  disabled={pos >= maxPos} />
        </div>
      </div>

      {/* Scrollable track */}
      <div
        ref={trackRef}
        onScroll={onScroll}
        className="flex gap-2 overflow-x-auto pb-2"
        style={{
          scrollbarWidth: 'none',      /* Firefox */
          msOverflowStyle: 'none',     /* IE */
          WebkitOverflowScrolling: 'touch',
        }}
      >
        <style>{`.poster-track::-webkit-scrollbar{display:none}`}</style>
        {posters.map((m, i) => (
          <PosterCard key={m.id || i} {...m} />
        ))}
      </div>
    </div>
  );
};

export default PosterSlider;
