import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { DefaultlayoutHoc } from '../layout/DefaultLayout';
import { bookingAPI, IMG_W500 } from '../services/api';
import { useAuth } from '../context/AuthContext';
import Loader from '../components/Loader/Loader';

const STATUS = {
  CONFIRMED: { bg: 'rgba(34,197,94,0.1)',  text: '#4ade80', border: 'rgba(34,197,94,0.2)'  },
  CANCELLED: { bg: 'rgba(239,68,68,0.1)',  text: '#f87171', border: 'rgba(239,68,68,0.2)'  },
  PENDING:   { bg: 'rgba(234,179,8,0.1)',  text: '#fbbf24', border: 'rgba(234,179,8,0.2)'  },
};

const fmtDate = dt => {
  try { return new Date(dt).toLocaleDateString('en-IN', { day: 'numeric', month: 'short', year: 'numeric', hour: '2-digit', minute: '2-digit' }); }
  catch { return dt; }
};

const BookingsPage = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState('');

  useEffect(() => {
    if (!user) { navigate('/'); return; }
    if (!user.id) {
      setError('User ID not found. Please logout and sign in again.');
      setLoading(false);
      return;
    }
    bookingAPI.getByUser(user.id)
      .then(r => setBookings(r.data || []))
      .catch(() => setError('Could not load bookings. Please check if the backend server is running.'))
      .finally(() => setLoading(false));
  }, [user, navigate]);

  const cancel = async (id) => {
    if (!window.confirm('Cancel this booking? This cannot be undone.')) return;
    try {
      await bookingAPI.cancel(id);
      setBookings(p => p.map(b => b.id === id ? { ...b, status: 'CANCELLED' } : b));
    } catch { alert('Cancellation failed. Please try again.'); }
  };

  if (loading) return <Loader />;

  return (
    <div className="container mx-auto px-4 py-10 max-w-4xl animate-fade-up">
      {/* Header */}
      <div className="flex items-center gap-3 mb-2">
        <div className="w-1 h-8 rounded-full" style={{ background: '#e51937' }} />
        <h1 className="text-white text-2xl font-black">My Bookings</h1>
      </div>
      <p className="text-gray-600 text-sm mb-8 pl-4">Complete history of your ticket bookings</p>

      {/* Error */}
      {error && (
        <div className="rounded-2xl px-5 py-4 mb-6 text-sm text-red-300 animate-fade-in"
          style={{ background: 'rgba(229,25,55,0.08)', border: '1px solid rgba(229,25,55,0.2)' }}>
          {error}
        </div>
      )}

      {/* Empty state */}
      {!loading && !error && bookings.length === 0 && (
        <div className="text-center py-24 animate-scale-in"
          style={{ background: '#14152b', borderRadius: 24, border: '1px solid rgba(255,255,255,0.04)' }}>
          <div className="w-20 h-20 rounded-2xl mx-auto mb-5 flex items-center justify-center"
            style={{ background: 'rgba(229,25,55,0.08)', border: '1px solid rgba(229,25,55,0.15)' }}>
            <svg viewBox="0 0 24 24" fill="none" stroke="#e51937" strokeWidth="1.5" width="36" height="36">
              <path strokeLinecap="round" strokeLinejoin="round" d="M16.5 6v.75m0 3v.75m0 3v.75m0 3V18m-9-5.25h5.25M7.5 15h3M3.375 5.25c-.621 0-1.125.504-1.125 1.125v3.026a2.999 2.999 0 010 5.198v3.026c0 .621.504 1.125 1.125 1.125h17.25c.621 0 1.125-.504 1.125-1.125v-3.026a3 3 0 010-5.198V6.375c0-.621-.504-1.125-1.125-1.125H3.375z" />
            </svg>
          </div>
          <h3 className="text-white text-lg font-bold mb-2">No bookings yet</h3>
          <p className="text-gray-600 text-sm mb-7">Explore movies and book your first ticket</p>
          <button onClick={() => navigate('/')}
            className="btn-red text-white px-8 py-3 rounded-xl font-bold text-sm">
            Browse Movies
          </button>
        </div>
      )}

      {/* Booking cards */}
      <div className="space-y-4">
        {bookings.map((b, i) => {
          const st = STATUS[b.status] || STATUS.PENDING;
          const poster = b.show?.movie?.posterUrl;
          const posterSrc = poster ? (poster.startsWith('http') ? poster : `${IMG_W500}${poster}`) : null;
          return (
            <div key={b.id} className="rounded-2xl overflow-hidden animate-fade-up"
              style={{ background: '#14152b', border: '1px solid rgba(255,255,255,0.05)', animationDelay: `${i * 0.05}s` }}>

              {/* Card header */}
              <div className="px-5 py-3 flex items-center justify-between"
                style={{ borderBottom: '1px solid rgba(255,255,255,0.05)', background: 'rgba(255,255,255,0.02)' }}>
                <div>
                  <p className="text-gray-600 text-xs mb-0.5">Booking Reference</p>
                  <p className="text-gray-400 text-xs font-mono truncate max-w-xs">{b.bookingNumber}</p>
                </div>
                <span className="text-xs font-bold px-3 py-1.5 rounded-full tracking-wide"
                  style={{ background: st.bg, color: st.text, border: `1px solid ${st.border}` }}>
                  {b.status}
                </span>
              </div>

              {/* Card body */}
              <div className="p-5 flex gap-4">
                {posterSrc && (
                  <img src={posterSrc} alt="" className="w-16 h-24 rounded-xl object-cover shrink-0"
                    onError={e => { e.target.style.display = 'none'; }} />
                )}
                <div className="flex-1 min-w-0">
                  <h3 className="text-white font-bold text-base mb-1 truncate">{b.show?.movie?.title || 'Unknown Movie'}</h3>
                  <p className="text-gray-500 text-xs mb-0.5">
                    {b.show?.screen?.theater?.name}{b.show?.screen?.name ? ` · ${b.show.screen.name}` : ''}
                  </p>
                  <p className="text-gray-600 text-xs mb-3">{fmtDate(b.show?.startTime)}</p>
                  <div className="flex flex-wrap gap-1">
                    {b.seats?.slice(0, 6).map(s => (
                      <span key={s.id} className="text-xs px-2 py-0.5 rounded-md font-mono"
                        style={{ background: 'rgba(255,255,255,0.05)', color: '#6b7280', border: '1px solid rgba(255,255,255,0.07)' }}>
                        {s.seat?.seatNumber}
                      </span>
                    ))}
                    {b.seats?.length > 6 && (
                      <span className="text-xs px-2 py-0.5 rounded-md" style={{ color: '#6b7280' }}>
                        +{b.seats.length - 6} more
                      </span>
                    )}
                  </div>
                </div>
                <div className="flex flex-col items-end justify-between shrink-0 gap-3">
                  <div className="text-right">
                    <p className="text-gray-600 text-xs mb-0.5">Total Paid</p>
                    <p className="text-white font-black text-xl">&#8377;{b.totalAmount?.toFixed(0)}</p>
                  </div>
                  <div className="flex flex-col items-end gap-2">
                    <p className="text-gray-700 text-xs">{fmtDate(b.bookingTime)}</p>
                    {b.status === 'CONFIRMED' && (
                      <button onClick={() => cancel(b.id)}
                        className="text-xs font-semibold px-3 py-1.5 rounded-lg transition-all hover:scale-105"
                        style={{ color: '#f87171', background: 'rgba(239,68,68,0.08)', border: '1px solid rgba(239,68,68,0.2)' }}>
                        Cancel
                      </button>
                    )}
                  </div>
