import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { IMG_ORIG } from '../../services/api';

const HeroCarousel = ({ backdrops = [] }) => {
  const [current, setCurrent] = useState(0);
  const [fading, setFading]   = useState(false);
  const navigate = useNavigate();

  const slides = backdrops.length ? backdrops : [];

  useEffect(() => {
    if (slides.length <= 1) return;
    const t = setInterval(() => {
      setFading(true);
      setTimeout(() => { setCurrent(c => (c + 1) % slides.length); setFading(false); }, 400);
    }, 4500);
    return () => clearInterval(t);
  }, [slides.length]);

  if (!slides.length) return null;

  const s = slides[current];
  const src = s.backdrop_path ? `${IMG_ORIG}${s.backdrop_path}` : (s.posterUrl?.startsWith('http') ? s.posterUrl : `https://image.tmdb.org/t/p/original${s.posterUrl}`);

  return (
    <div className="relative overflow-hidden" style={{ height: '72vh', minHeight: 440, maxHeight: 680 }}>

      <div
        className="absolute inset-0 bg-cover bg-center transition-opacity duration-500"
        style={{ backgroundImage: `url(${src})`, opacity: fading ? 0 : 1 }}
      />

      {/* Gradient overlays */}
      <div className="absolute inset-0" style={{ background: 'linear-gradient(to right, rgba(0,0,0,0.85) 0%, rgba(0,0,0,0.4) 60%, rgba(0,0,0,0.1) 100%)' }} />
      <div className="absolute inset-0" style={{ background: 'linear-gradient(to top, rgba(13,14,26,1) 0%, transparent 40%)' }} />

      {/* Content */}
      <div className="absolute inset-0 flex items-center px-8 md:px-16">
        <div key={current} className="text-white max-w-xl">
          <div className="live-badge text-green-400 text-xs font-semibold mb-4 animate-fade-up">
            NOW SHOWING
          </div>
          <h1 className="text-4xl md:text-5xl lg:text-6xl font-black mb-3 leading-tight animate-fade-up delay-100"
            style={{ textShadow: '0 4px 30px rgba(0,0,0,0.8)', letterSpacing: '-1px' }}>
            {s.title}
          </h1>
          {s.description && (
            <p className="text-gray-300 text-sm md:text-base mb-6 line-clamp-2 animate-fade-up delay-200 leading-relaxed">
              {s.description}
            </p>
          )}
          <div className="flex gap-3 animate-fade-up delay-300">
            <button
              onClick={() => navigate(`/movie/${s.id}`)}
              className="btn-red text-white px-8 py-3 rounded-xl font-bold text-sm"
            >
              Book Tickets
            </button>
            <button
              onClick={() => navigate(`/movie/${s.id}`)}
              className="text-white px-6 py-3 rounded-xl font-semibold text-sm transition-colors"
              style={{ background: 'rgba(255,255,255,0.1)', border: '1px solid rgba(255,255,255,0.2)' }}
            >
              More Info
            </button>
          </div>
        </div>
      </div>

      {/* Dots */}
      <div className="absolute bottom-8 left-1/2 -translate-x-1/2 flex gap-2">
        {slides.map((_, i) => (
          <button
            key={i}
            onClick={() => { setFading(true); setTimeout(() => { setCurrent(i); setFading(false); }, 300); }}
            className="transition-all duration-300 rounded-full"
            style={{ width: i === current ? 28 : 8, height: 8, background: i === current ? '#e51937' : 'rgba(255,255,255,0.3)' }}
          />
        ))}
      </div>

      {/* Thumbnail strip */}
      <div className="absolute right-6 top-1/2 -translate-y-1/2 hidden xl:flex flex-col gap-3">
        {slides.map((sl, i) => {
          const tsrc = sl.backdrop_path ? `https://image.tmdb.org/t/p/w300${sl.backdrop_path}` : `https://image.tmdb.org/t/p/w200${sl.posterUrl}`;
          return (
            <button key={i} onClick={() => { setFading(true); setTimeout(() => { setCurrent(i); setFading(false); }, 300); }}
              className="rounded-lg overflow-hidden transition-all duration-300"
              style={{ width: 90, height: 52, border: i === current ? '2px solid #e51937' : '2px solid rgba(255,255,255,0.15)', opacity: i === current ? 1 : 0.5 }}>
              <img src={tsrc} alt="" className="w-full h-full object-cover" />
            </button>
          );
        })}
      </div>
    </div>
  );
};

export default HeroCarousel;
