import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { DefaultlayoutHoc } from '../layout/DefaultLayout';
import { bookingAPI, IMG_W500 } from '../services/api';
// Load streaming purchases saved by PaymentModal into localStorage
const loadLocalBookings = (user) => {
  if (!user) return [];
  try {
    return JSON.parse(localStorage.getItem(`bms_bookings_${user.id || user.email}`) || '[]');
  } catch { return []; }
};
import { useAuth } from '../context/AuthContext';
import Loader from '../components/Loader/Loader';

const STATUS_STYLE = {
  CONFIRMED: { bg: 'rgba(34,197,94,0.1)',  text: '#4ade80', border: 'rgba(34,197,94,0.2)'  },
  CANCELLED: { bg: 'rgba(239,68,68,0.1)',  text: '#f87171', border: 'rgba(239,68,68,0.2)'  },
  PENDING:   { bg: 'rgba(234,179,8,0.1)',  text: '#fbbf24', border: 'rgba(234,179,8,0.2)'  },
};
const TYPE_BADGE = {
  STREAMING: { bg: 'rgba(139,92,246,0.12)', text: '#c4b5fd', border: 'rgba(139,92,246,0.25)', label: 'Streaming' },
  THEATER:   { bg: 'rgba(59,130,246,0.12)', text: '#93c5fd', border: 'rgba(59,130,246,0.25)', label: 'Theater'   },
};

const fmtDate = dt => {
  try { return new Date(dt).toLocaleDateString('en-IN', { day: 'numeric', month: 'short', year: 'numeric', hour: '2-digit', minute: '2-digit' }); }
  catch { return dt; }
};

const BookingsPage = () => {
  const { user }   = useAuth();
  const navigate   = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading,  setLoading]  = useState(true);

  useEffect(() => {
    if (!user) { navigate('/'); return; }

    const localBookings = loadLocalBookings(user);
    setBookings(localBookings);               // show immediately, no flicker

    // Then try to fetch from backend and merge
    const fetchFromAPI = async () => {
      try {
        if (!user.id) throw new Error('no user.id');
        const res   = await bookingAPI.getByUser(user.id);
        const api   = res.data || [];
        const local = loadLocalBookings(user);
        const localNums = new Set(local.map(b => b.bookingNumber));
        const merged = [...local, ...api.filter(b => !localNums.has(b.bookingNumber))];
        setBookings(merged);
      } catch {
        // Backend unavailable — already showing local bookings, that's fine
      } finally {
        setLoading(false);
      }
    };
    fetchFromAPI();
  }, [user, navigate]);

  const handleCancel = async (b) => {
    if (!window.confirm('Cancel this booking? This cannot be undone.')) return;
    // Try API cancel
    try {
      if (b.id && typeof b.id === 'number') await bookingAPI.cancel(b.id);
    } catch {}
    // Always update localStorage too
    // Update localStorage
    const key = `bms_bookings_${user.id || user.email}`;
    try {
      const saved = JSON.parse(localStorage.getItem(key) || "[]");
      const updated = saved.map(x => x.bookingNumber === b.bookingNumber ? { ...x, status: "CANCELLED" } : x);
      localStorage.setItem(key, JSON.stringify(updated));
    } catch {}
    setBookings(p => p.map(x => x.bookingNumber === b.bookingNumber ? { ...x, status: 'CANCELLED' } : x));
  };

  if (loading && bookings.length === 0) return <Loader />;

  return (
    <div className="container mx-auto px-4 py-10 max-w-4xl animate-fade-up">
      {/* Header */}
      <div className="flex items-center gap-3 mb-1">
        <div className="w-1 h-8 rounded-full" style={{ background: '#e51937' }} />
        <h1 className="text-white text-2xl font-black">My Bookings</h1>
        {bookings.length > 0 && (
          <span className="px-2.5 py-0.5 rounded-full text-xs font-bold text-gray-400"
            style={{ background: 'rgba(255,255,255,0.06)', border: '1px solid rgba(255,255,255,0.08)' }}>
            {bookings.length}
          </span>
        )}
      </div>
      <p className="text-gray-600 text-sm mb-8 pl-4">All your tickets and streaming purchases</p>

      {/* Empty state */}
      {!loading && bookings.length === 0 && (
        <div className="text-center py-24 animate-scale-in rounded-2xl"
          style={{ background: '#14152b', border: '1px solid rgba(255,255,255,0.04)' }}>
          <div className="w-20 h-20 rounded-2xl mx-auto mb-5 flex items-center justify-center"
            style={{ background: 'rgba(229,25,55,0.08)', border: '1px solid rgba(229,25,55,0.15)' }}>
            <svg viewBox="0 0 24 24" fill="none" stroke="#e51937" strokeWidth="1.5" width="36" height="36">
              <path strokeLinecap="round" strokeLinejoin="round" d="M16.5 6v.75m0 3v.75m0 3v.75m0 3V18m-9-5.25h5.25M7.5 15h3M3.375 5.25c-.621 0-1.125.504-1.125 1.125v3.026a2.999 2.999 0 010 5.198v3.026c0 .621.504 1.125 1.125 1.125h17.25c.621 0 1.125-.504 1.125-1.125v-3.026a3 3 0 010-5.198V6.375c0-.621-.504-1.125-1.125-1.125H3.375z"/>
            </svg>
          </div>
          <h3 className="text-white text-lg font-bold mb-2">No bookings yet</h3>
          <p className="text-gray-600 text-sm mb-7">Explore movies and book your first ticket</p>
          <button onClick={() => navigate('/')}
            className="btn-red text-white px-8 py-3 rounded-xl font-bold text-sm">
            Browse Movies
          </button>
        </div>
      )}

      {/* Booking cards */}
      <div className="space-y-4">
        {bookings.map((b, i) => {
          const st      = STATUS_STYLE[b.status] || STATUS_STYLE.PENDING;
          const typeBadge = TYPE_BADGE[b.type]   || TYPE_BADGE.THEATER;
          const poster  = b.show?.movie?.posterUrl;
          const posterSrc = poster
            ? (poster.startsWith('http') ? poster : `${IMG_W500}${poster}`)
            : null;
          const isStreaming = b.type === 'STREAMING';

          return (
            <div key={b.bookingNumber || b.id || i}
              className="rounded-2xl overflow-hidden animate-fade-up"
              style={{ background: '#14152b', border: '1px solid rgba(255,255,255,0.05)', animationDelay: `${i * 0.04}s` }}>

              {/* Card header */}
              <div className="px-5 py-3 flex items-center justify-between gap-3"
                style={{ borderBottom: '1px solid rgba(255,255,255,0.05)', background: 'rgba(255,255,255,0.02)' }}>
                <div className="min-w-0">
                  <p className="text-gray-600 text-xs mb-0.5">Booking Reference</p>
                  <p className="text-gray-300 text-xs font-mono truncate">{b.bookingNumber}</p>
                </div>
                <div className="flex items-center gap-2 shrink-0">
                  {/* Type badge */}
                  <span className="text-xs font-bold px-2.5 py-1 rounded-full"
                    style={{ background: typeBadge.bg, color: typeBadge.text, border: `1px solid ${typeBadge.border}` }}>
                    {typeBadge.label}
                  </span>
                  {/* Status badge */}
                  <span className="text-xs font-bold px-2.5 py-1 rounded-full"
                    style={{ background: st.bg, color: st.text, border: `1px solid ${st.border}` }}>
                    {b.status}
                  </span>
                </div>
              </div>

              {/* Card body */}
              <div className="p-5 flex gap-4">
                {/* Poster */}
                {posterSrc && (
                  <img src={posterSrc} alt=""
                    className="w-16 h-24 rounded-xl object-cover shrink-0"
                    onError={e => { e.target.style.display = 'none'; }} />
                )}

                <div className="flex-1 min-w-0">
                  <h3 className="text-white font-bold text-base mb-1 truncate">
                    {b.show?.movie?.title || 'Unknown Movie'}
                  </h3>

                  {isStreaming ? (
                    /* Streaming booking info */
                    <>
                      <div className="flex items-center gap-2 mb-2">
                        <span className="text-xs px-2 py-0.5 rounded-full font-semibold"
                          style={b.purchaseType === 'RENT'
                            ? { background: 'rgba(59,130,246,0.15)', color: '#93c5fd' }
                            : { background: 'rgba(139,92,246,0.15)', color: '#c4b5fd' }}>
                          {b.purchaseType === 'RENT' ? 'Rented · 48hr access' : 'Purchased · Lifetime'}
                        </span>
                      </div>
                      <p className="text-gray-600 text-xs">{b.show?.movie?.genre} · {b.show?.movie?.language}</p>
                    </>
                  ) : (
                    /* Theater booking info */
                    <>
                      <p className="text-gray-500 text-xs mb-0.5">
                        {b.show?.screen?.theater?.name}
                        {b.show?.screen?.name ? ` · ${b.show.screen.name}` : ''}
                      </p>
                      <p className="text-gray-600 text-xs mb-2">{fmtDate(b.show?.startTime)}</p>
                      {/* Seat chips */}
                      <div className="flex flex-wrap gap-1">
                        {(b.seats || []).slice(0, 8).map(s => (
                          <span key={s.id || s.seat?.seatNumber}
                            className="text-xs px-1.5 py-0.5 rounded font-mono"
                            style={{ background: 'rgba(255,255,255,0.05)', color: '#6b7280', border: '1px solid rgba(255,255,255,0.07)' }}>
                            {s.seat?.seatNumber}
                          </span>
                        ))}
                        {(b.seats || []).length > 8 && (
                          <span className="text-xs px-1.5 py-0.5 text-gray-600">
                            +{b.seats.length - 8} more
                          </span>
                        )}
                      </div>
                    </>
                  )}
                </div>

                {/* Right side */}
                <div className="flex flex-col items-end justify-between shrink-0 gap-2">
                  <div className="text-right">
                    <p className="text-gray-600 text-xs">Total Paid</p>
                    <p className="text-white font-black text-xl">&#8377;{b.totalAmount?.toFixed(0)}</p>
                  </div>
                  <div className="text-right">
                    <p className="text-gray-700 text-xs mb-2">{fmtDate(b.bookingTime)}</p>
                    {b.status === 'CONFIRMED' && (
                      <button onClick={() => handleCancel(b)}
                        className="text-xs font-semibold px-3 py-1.5 rounded-lg transition-all hover:scale-105"
                        style={{ color: '#f87171', background: 'rgba(239,68,68,0.08)', border: '1px solid rgba(239,68,68,0.2)' }}>
                        Cancel
                      </button>
                    )}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default DefaultlayoutHoc(BookingsPage);
