import React from 'react';
import Slider from 'react-slick';
import Poster from '../Poster/Poster';

const DEFAULT = {
  arrows: true, infinite: false, slidesToShow: 6, slidesToScroll: 3, speed: 500,
  responsive: [
    { breakpoint: 1536, settings: { slidesToShow: 5, slidesToScroll: 3 } },
    { breakpoint: 1280, settings: { slidesToShow: 4, slidesToScroll: 3 } },
    { breakpoint: 1024, settings: { slidesToShow: 3, slidesToScroll: 2, infinite: true, dots: true } },
    { breakpoint: 640,  settings: { slidesToShow: 2, slidesToScroll: 2 } },
  ],
};

const PosterSlider = ({ title, subtitle, posters = [], config }) => {
  if (!posters.length) return null;
  return (
    <div className="animate-fade-up">
      <div className="flex items-end justify-between mb-5">
        <div>
          <h3 className="text-white text-xl font-bold">{title}</h3>
          {subtitle && <p className="text-gray-500 text-xs mt-0.5">{subtitle}</p>}
        </div>
        <button className="text-red-400 hover:text-red-300 text-sm font-medium transition-colors">
          See all
        </button>
      </div>
      <Slider {...(config || DEFAULT)}>
        {posters.map((m, i) => <Poster key={m.id || i} {...m} />)}
      </Slider>
    </div>
  );
};

export default PosterSlider;
